import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Home from "./components/Home";
import Transactions from "./components/Transactions";
import Deposit from "./components/Deposit";
import Navbar from "./components/Navbar";
import Goals from "./components/Goals";
import AuthPage from "./components/AuthPage"; 

function App() {
  // Check if user is authenticated (using localStorage for now)
  const isAuthenticated = localStorage.getItem("isAuthenticated");

  return (
    <Router>
      {/* Only show Navbar if user is logged in */}
      {isAuthenticated && <Navbar />}

      <div className="container">
        <Routes>
          {/* If user is not logged in, redirect to AuthPage */}
          <Route path="/" element={isAuthenticated ? <Home /> : <Navigate to="/auth" />} />
          <Route path="/transactions" element={isAuthenticated ? <Transactions /> : <Navigate to="/auth" />} />
          <Route path="/deposit" element={isAuthenticated ? <Deposit /> : <Navigate to="/auth" />} />
          <Route path="/goals" element={isAuthenticated ? <Goals /> : <Navigate to="/auth" />} />
          <Route path="/auth" element={<AuthPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
