import React, { useState, useEffect } from "react";

function GoalsForm() {
  const [goalName, setGoalName] = useState("");
  const [goalAmount, setGoalAmount] = useState("");
  const [goals, setGoals] = useState([]);
  const [transactions, setTransactions] = useState([]);

  // Fetch goals from the server
  const fetchGoals = async () => {
    const response = await fetch("http://127.0.0.1:5000/goals");
    const data = await response.json();
    setGoals(data);
  };

  // Fetch transactions for progress calculation
  const fetchTransactions = async () => {
    const response = await fetch("http://127.0.0.1:5000/transactions");
    const data = await response.json();
    setTransactions(data);
  };

  useEffect(() => {
    fetchGoals();
    fetchTransactions();
  }, []);

  // Calculate progress for each goal
  const calculateProgress = (goalId) => {
    const totalDeposited = transactions.reduce((total, tx) => {
      return tx.goal_id === goalId ? total + tx.amount : total;
    }, 0);
    return totalDeposited; // You can return a percentage if you want
  };

  const handleCreateGoal = async () => {
    if (!goalName || !goalAmount) {
      alert("Please enter both goal name and amount.");
      return;
    }

    const newGoal = {
      goal_name: goalName,
      goal_amount: parseFloat(goalAmount),
    };

    const response = await fetch("http://127.0.0.1:5000/set-goal", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newGoal),
    });

    if (response.ok) {
      alert("Goal created successfully!");
      setGoalName("");
      setGoalAmount("");
      fetchGoals(); // Refresh goal list after adding a new goal
    } else {
      alert("Failed to create goal.");
    }
  };

  return (
    <div className="goal-container">
      <h2>Create a New Goal</h2>
      <div className="input-group">
        <input
          type="text"
          placeholder="Goal Name"
          value={goalName}
          onChange={(e) => setGoalName(e.target.value)}
          className="goal-input"
        />
      </div>
      <div className="input-group">
        <span className="currency-symbol">$</span>
        <input
          type="number"
          placeholder="Goal Amount"
          value={goalAmount}
          onChange={(e) => setGoalAmount(e.target.value)}
          className="goal-input"
        />
      </div>
      <button onClick={handleCreateGoal} className="goal-button">
        Set Goal
      </button>

      <h3>My Goals</h3>
      <div className="goals-list">
        {goals.length > 0 ? (
          goals.map((goal) => (
            <div className="goal-card" key={goal.id}>
              <h4>{goal.goal_name}</h4>
              <p>Goal Amount: ${goal.goal_amount}</p>
              <p>Progress: ${calculateProgress(goal.id)}</p>
            </div>
          ))
        ) : (
          <p>No goals set yet.</p>
        )}
      </div>
    </div>
  );
}

export default GoalsForm;
