import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Home from "./components/Home";
import Transactions from "./components/Transactions";
import Deposit from "./components/Deposit";
import Navbar from "./components/Navbar";
import Goals from "./components/Goals";
import AuthPage from "./components/AuthPage";

function PrivateRoute({ element }) {
  const token = localStorage.getItem("token");
  return token ? element : <Navigate to="/auth" replace />;
}

function App() {
  return (
    <Router>
      <Navbar /> {/* Navbar visible on all pages */}
      <Routes>
        <Route path="/auth" element={<AuthPage />} />
        {/* Redirect all routes to the login page for testing */}
        <Route path="/" element={<Navigate to="/auth" replace />} />
        <Route path="/transactions" element={<Navigate to="/auth" replace />} />
        <Route path="/deposit" element={<Navigate to="/auth" replace />} />
        <Route path="/goals" element={<Navigate to="/auth" replace />} />
        {/* Redirect any undefined routes to auth */}
        <Route path="*" element={<Navigate to="/auth" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
