import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Home from "./components/Home";
import Transactions from "./components/Transactions";
import Deposit from "./components/Deposit";
import Navbar from "./components/Navbar";
import Goals from "./components/Goals";
import AuthPage from "./components/AuthPage";

function PrivateRoute({ element }) {
  const token = localStorage.getItem("token");
  return token ? element : <Navigate to="/auth" replace />;
}

function App() {
  // Get token status to determine redirection
  const token = localStorage.getItem("token");

  return (
    <Router>
      <Navbar /> {/* Place Navbar here so it appears on all pages */}
      <Routes>
        <Route path="/auth" element={<AuthPage />} />
        {token ? (
          // If token exists, show private routes
          <>
            <Route path="/" element={<Home />} />
            <Route path="/transactions" element={<PrivateRoute element={<Transactions />} />} />
            <Route path="/deposit" element={<PrivateRoute element={<Deposit />} />} />
            <Route path="/goals" element={<PrivateRoute element={<Goals />} />} />
          </>
        ) : (
          // If no token, redirect to login
          <Route path="/" element={<Navigate to="/auth" replace />} />
        )}
        {/* Add a catch-all route for 404 page if desired */}
        <Route path="*" element={<Navigate to="/auth" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
