import React, { useState, useEffect } from "react";

function GoalsForm() {
  const [goalName, setGoalName] = useState("");
  const [goalAmount, setGoalAmount] = useState("");
  const [goals, setGoals] = useState([]); // Local state for storing fetched goals

  // Function to fetch goals from the server
  const fetchGoals = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/goals");
      if (!response.ok) throw new Error("Failed to fetch goals");

      const data = await response.json();
      setGoals(data); // Update state with the fetched goals
    } catch (error) {
      console.error("Error fetching goals:", error);
    }
  };

  // Fetch goals on component mount
  useEffect(() => {
    fetchGoals();
  }, []);

  // Function to create a new goal
  const handleCreateGoal = async () => {
    if (!goalName || !goalAmount) {
      alert("Please enter both goal name and amount.");
      return;
    }

    const newGoal = {
      goal_name: goalName,
      goal_amount: parseFloat(goalAmount),
    };

    try {
      const response = await fetch("http://127.0.0.1:5000/set-goal", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newGoal),
      });

      if (response.ok) {
        alert("Goal created successfully!");
        setGoalName("");
        setGoalAmount("");
        fetchGoals(); // Refresh goal list after adding a new goal
      } else {
        alert("Failed to create goal.");
      }
    } catch (error) {
      console.error("Error creating goal:", error);
    }
  };

  return (
    <div className="goal-container">
      <h2>Create a New Goal</h2>

      {/* Goal Name Input */}
      <div className="input-group">
        <input
          type="text"
          placeholder="Goal Name"
          value={goalName}
          onChange={(e) => setGoalName(e.target.value)}
          className="goal-input"
        />
      </div>

      {/* Goal Amount Input */}
      <div className="input-group">
        <span className="currency-symbol">$</span>
        <input
          type="number"
          placeholder="Goal Amount"
          value={goalAmount}
          onChange={(e) => setGoalAmount(e.target.value)}
          className="goal-input"
        />
      </div>

      <button onClick={handleCreateGoal} className="goal-button">
        Set Goal
      </button>

      {/* Display Goals List */}
      <h3>My Goals</h3>
      <ul>
        {goals.length > 0 ? (
          goals.map((goal, index) => (
            <li key={index}>
              {goal.goal_name}: ${goal.goal_amount}
            </li>
          ))
        ) : (
          <p>No goals set yet.</p>
        )}
      </ul>
    </div>
  );
}

export default GoalsForm;
