import React, { useState, useEffect } from "react";

function TransactionForm({ fetchGoals }) {
  const [amount, setAmount] = useState("");
  const [note, setNote] = useState("");
  const [isDeposit, setIsDeposit] = useState(true); // Toggle between deposit/withdraw
  const [goals, setGoals] = useState([]); // Goals state
  const [selectedGoal, setSelectedGoal] = useState(""); // Selected goal state
  const [error, setError] = useState(""); // Error message state

  // Fetch goals when the component mounts
  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/goals");
        if (!response.ok) throw new Error("Failed to fetch goals");
        const data = await response.json();
        setGoals(data);
      } catch (error) {
        console.error("Error fetching goals:", error);
      }
    };
    fetchGoals();
  }, []);

  const handleTransaction = async () => {
    if (isDeposit && (!selectedGoal || amount <= 0)) {
      setError("Please select a goal and enter a valid amount.");
      return;
    }

    const transaction = {
      amount: isDeposit ? parseFloat(amount) : -parseFloat(amount), // Negative for withdrawals
      note,
      goal_id: isDeposit ? selectedGoal : null, // Include goal ID for deposits
    };

    try {
      const response = await fetch("http://127.0.0.1:5000/add-transaction", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(transaction),
      });

      if (response.ok) {
        // Update current progress in the goal after a deposit
        if (isDeposit) {
          await fetch(`http://127.0.0.1:5000/update-goal-progress`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              goalId: selectedGoal,
              amount: parseFloat(amount), // Amount deposited
            }),
          });
        }

        alert(`${isDeposit ? "Deposit" : "Withdrawal"} Successful!`);
        setAmount("");
        setNote("");
        setSelectedGoal(""); // Reset selected goal
        setError(""); // Clear any error messages
        fetchGoals(); // Refresh goals to show updated progress
      } else {
        alert(`${isDeposit ? "Deposit" : "Withdrawal"} Failed`);
      }
    } catch (error) {
      console.error("Error during transaction:", error);
    }
  };

  return (
    <div className="transaction-container">
      <h2>{isDeposit ? "Enter an amount to deposit" : "Enter an amount to withdraw"}</h2>

      {/* Toggle Buttons */}
      <div className="toggle-buttons">
        <button className={isDeposit ? "active" : ""} onClick={() => setIsDeposit(true)}>Deposit</button>
        <button className={!isDeposit ? "active" : ""} onClick={() => setIsDeposit(false)}>Withdraw</button>
      </div>

      {/* Goal Selection */}
      {isDeposit && (
        <div className="input-group">
          <select
            value={selectedGoal}
            onChange={(e) => setSelectedGoal(e.target.value)}
          >
            <option value="" disabled>Select a Goal</option>
            {goals.map((goal) => (
              <option key={goal.id} value={goal.id}>{goal.goal_name}</option>
            ))}
          </select>
        </div>
      )}

      {/* Amount Input */}
      <div className="input-group">
        <span className="currency-symbol">$</span>
        <input
          type="number"
          placeholder="0.00"
          value={amount}
          onChange={(e) => {
            setAmount(e.target.value);
            setError(""); // Clear error message on input change
          }}
          className="transaction-input"
        />
      </div>

      {/* Note Input */}
      <div className="input-group">
        <input
          type="text"
          placeholder="Add a note (optional)"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          className="transaction-input"
        />
      </div>

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      <button onClick={handleTransaction} className={isDeposit ? "deposit-button" : "withdraw-button"}>
        {isDeposit ? "Deposit" : "Withdraw"}
      </button>
    </div>
  );
}

export default TransactionForm;
