import React, { useState, useEffect } from "react";

function GoalsPage() {
  const [goalName, setGoalName] = useState("");
  const [goalAmount, setGoalAmount] = useState("");
  const [goals, setGoals] = useState([]);

  // Fetch goals from the backend
  const fetchGoals = async () => {
    const response = await fetch("http://127.0.0.1:5000/goals");
    if (response.ok) {
      const data = await response.json();
      setGoals(data);
    } else {
      alert("Failed to fetch goals");
    }
  };

  // Create a new goal
  const createGoal = async () => {
    if (!goalName || !goalAmount) {
      alert("Please enter both goal name and amount.");
      return;
    }

    const newGoal = {
      goal_name: goalName,
      goal_amount: parseFloat(goalAmount),
    };

    const response = await fetch("http://127.0.0.1:5000/api/set_goal", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newGoal),
    });

    if (response.ok) {
      alert("Goal created successfully!");
      setGoalName("");
      setGoalAmount("");
      fetchGoals(); // Refresh the list of goals
    } else {
      alert("Failed to create goal. Try again.");
    }
  };

  // UseEffect hook to fetch goals when the component mounts
  useEffect(() => {
    fetchGoals();
  }, []);

  return (
    <div className="goals-container">
      <h1>Your Goals</h1>

      {/* Create Goal Form */}
      <div className="goal-form">
        <h2>Create New Goal</h2>
        <input
          type="text"
          placeholder="Goal Name"
          value={goalName}
          onChange={(e) => setGoalName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Goal Amount"
          value={goalAmount}
          onChange={(e) => setGoalAmount(e.target.value)}
        />
        <button onClick={createGoal}>Set Goal</button>
      </div>

      {/* Goals List */}
      <div className="goal-list">
        <h2>Your Goals List</h2>
        {goals.length === 0 ? (
          <p>No goals set yet!</p>
        ) : (
          goals.map((goal) => (
            <div key={goal.goal_id} className="goal-item">
              <h3>{goal.goal_name}</h3>
              <p>Goal Amount: ${goal.goal_amount}</p>
              <p>Deposited: ${goal.deposited_amount}</p>
              <p>Remaining: ${goal.remaining}</p>
              <p>Status: {goal.deposited_amount >= goal.goal_amount ? "Completed" : "Pending"}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default GoalsPage;
