import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from "react-router-dom";
import Home from "./components/Home";
import Transactions from "./components/Transactions";
import Deposit from "./components/Deposit";
import Navbar from "./components/Navbar";
import Goals from "./components/Goals";
import AuthPage from "./components/AuthPage";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(localStorage.getItem("isAuthenticated"));

  useEffect(() => {
    // Update authentication state on storage change
    const handleStorageChange = () => setIsAuthenticated(localStorage.getItem("isAuthenticated"));
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  // Hook to get the current route
  const location = useLocation();

  return (
    <Router>
      <div className="container">
        {/* âœ… Hide Navbar when on the auth page */}
        {isAuthenticated && location.pathname !== "/auth" && <Navbar />}

        <Routes>
          {/* Redirect to auth page if not logged in */}
          <Route path="/" element={isAuthenticated ? <Home /> : <Navigate to="/auth" />} />
          <Route path="/transactions" element={isAuthenticated ? <Transactions /> : <Navigate to="/auth" />} />
          <Route path="/deposit" element={isAuthenticated ? <Deposit /> : <Navigate to="/auth" />} />
          <Route path="/goals" element={isAuthenticated ? <Goals /> : <Navigate to="/auth" />} />
          {/* The Auth page is always accessible */}
          <Route path="/auth" element={<AuthPage />} />
          {/* Redirect any unknown routes to /auth */}
          <Route path="*" element={<Navigate to="/auth" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
