import React, { useState, useEffect } from "react";

function TransactionForm() {
  const [amount, setAmount] = useState("");
  const [note, setNote] = useState("");
  const [isDeposit, setIsDeposit] = useState(true);
  const [goals, setGoals] = useState([]); // Local state to store goals
  const [selectedGoal, setSelectedGoal] = useState(""); // State for the selected goal

  // Fetch goals for the dropdown
  const fetchGoals = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/goals");
      if (!response.ok) throw new Error("Failed to fetch goals");
      const data = await response.json();
      setGoals(data);
    } catch (error) {
      console.error("Error fetching goals:", error);
    }
  };

  useEffect(() => {
    fetchGoals();
  }, []);

  const handleTransaction = async () => {
    const transaction = {
      amount: isDeposit ? parseFloat(amount) : -parseFloat(amount),
      note,
      goal_id: selectedGoal, // Include selected goal
    };

    const response = await fetch("http://127.0.0.1:5000/add-transaction", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(transaction),
    });

    if (response.ok) {
      alert(`${isDeposit ? "Deposit" : "Withdrawal"} Successful!`);
      setAmount("");
      setNote("");
      setSelectedGoal(""); // Reset selected goal
    } else {
      alert(`${isDeposit ? "Deposit" : "Withdrawal"} Failed`);
    }
  };

  return (
    <div className="transaction-container">
      <h2>{isDeposit ? "Enter an amount to deposit" : "Enter an amount to withdraw"}</h2>
      
      {/* Toggle Buttons */}
      <div className="toggle-buttons">
        <button className={isDeposit ? "active" : ""} onClick={() => setIsDeposit(true)}>Deposit</button>
        <button className={!isDeposit ? "active" : ""} onClick={() => setIsDeposit(false)}>Withdraw</button>
      </div>

      {/* Amount Input */}
      <div className="input-group">
        <span className="currency-symbol">$</span>
        <input
          type="number"
          placeholder="0.00"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="transaction-input"
        />
      </div>

      {/* Note Input */}
      <div className="input-group">
        <input
          type="text"
          placeholder="Add a note (optional)"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          className="transaction-input"
        />
      </div>

      {/* Goal Selector */}
      <div className="input-group">
        <select value={selectedGoal} onChange={(e) => setSelectedGoal(e.target.value)} className="goal-select">
          <option value="">Select a goal</option>
          {goals.map((goal) => (
            <option key={goal.id} value={goal.id}>{goal.goal_name}</option>
          ))}
        </select>
      </div>

      <button onClick={handleTransaction} className={isDeposit ? "deposit-button" : "withdraw-button"}>
        {isDeposit ? "Deposit" : "Withdraw"}
      </button>
    </div>
  );
}

export default TransactionForm;
