import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from "react-router-dom";
import Home from "./components/Home";
import Transactions from "./components/Transactions";
import Deposit from "./components/Deposit";
import Navbar from "./components/Navbar";
import Goals from "./components/Goals";
import AuthPage from "./components/AuthPage";

function PrivateRoute({ element }) {
  const token = localStorage.getItem("token");
  return token ? element : <Navigate to="/auth" replace />;
}

function App() {
  const location = useLocation();

  return (
    <>
      {/* Render Navbar only if not on the auth route */}
      {!location.pathname.includes("/auth") && <Navbar />}
      <Routes>
        <Route path="/auth" element={<AuthPage />} />
        <Route path="/" element={<Navigate to="/auth" replace />} />
        <Route path="/home" element={<PrivateRoute element={<Home />} />} />
        <Route path="/transactions" element={<PrivateRoute element={<Transactions />} />} />
        <Route path="/deposit" element={<PrivateRoute element={<Deposit />} />} />
        <Route path="/goals" element={<PrivateRoute element={<Goals />} />} />
        {/* Redirect any undefined routes to auth */}
        <Route path="*" element={<Navigate to="/auth" replace />} />
      </Routes>
    </>
  );
}

export default function RouterWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
